import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { dark } from 'react-syntax-highlighter/dist/esm/styles/prism';

export default function CodeBlock({ language, value }) {
	return (
		<SyntaxHighlighter
			language={language} 
			showLineNumbers
			showInlineLineNumbers
			style={dark}
		>
			{value}
		</SyntaxHighlighter>
	);
}
